        if (buffer.X.length > 0){
            campaignTimestamp = buffer.X[0].campaignTimestamp;
        }
        else if (buffer.Y.length > 0){
            campaignTimestamp = buffer.Y[0].campaignTimestamp;
        }
        else if (buffer.Z.length > 0){
            campaignTimestamp = buffer.Z[0].campaignTimestamp;
        }

        //the following is based on
        //https://discourse.nodered.org/t/manipulate-vector-for-dashboard-chart-node/25298/3

        //1. lets create a variable to hold the final chart data...
        let chartData = [];  //See #1 - create an empty array

        //2. lets create a variable to hold the observations...
        let element1 = {};  //create element1 as an empty object {}

        //3, now add the .property "series" to the element1 {object}
        //We can create several series to plot different lines in the chart
        element1.series = []; // add a .property called series of type [array]
        element1.series.push(measurement + " X"); //add 1st array element "Acceleration"
        element1.series.push(measurement + " Y"); //add 1st array element "Acceleration"
        element1.series.push(measurement + " Z"); //add 1st array element "Acceleration"

        //4. now add the .property "data" to the element1 {object}
        element1.data = []; // add a .property called data of type [array]

        //4b. now we add 3 arrays to the data [array]
        //In here, we can have several arrays, one for every series we have
        var dataElementX = [];
        var dataElementY = [];
        var dataElementZ = [];

        //4c. now add the 3 {objects} inside each of the dataElements
        for(let observation of buffer.X){
            switch (measurement){
                case "Frequencies":
                    dataElementX.push( {  //add the observation values
                        "x": observation.value1,  
                        "y": observation.value2 
                    });
                    break;
                default:
                    dataElementX.push( {  //add the observation values
                        "x": observation.timestamp,  
                        "y": observation.result 
                    });
            }
        }
        for(let observation of buffer.Y){
            switch (measurement){
                case "Frequencies":
                    dataElementY.push( {  //add the observation values
                        "x": observation.value1,  
                        "y": observation.value2 
                    });
                    break;
                default:
                    dataElementY.push( {  //add the observation values
                        "x": observation.timestamp,  
                        "y": observation.result 
                    });
            }
        }
        for(let observation of buffer.Z){
            switch (measurement){
                case "Frequencies":
                    dataElementZ.push( {  //add the observation values
                        "x": observation.value1,  
                        "y": observation.value2 
                    });
                    break;
                default:
                    dataElementZ.push( {  //add the observation values
                        "x": observation.timestamp,  
                        "y": observation.result 
                    });
            }
        }

        //now we need to push the dataelements into the data [array]
        element1.data.push(dataElementX);
        element1.data.push(dataElementY);
        element1.data.push(dataElementZ);

        /*******************************************/

        //5. now add a labels [array] to element1
        element1.labels = []; // add a .property called labels of type [array]
        element1.labels.push("X");
        element1.labels.push("Y");
        element1.labels.push("Z");

        //Now add the element1 [array] into the chartData [array]
        chartData.push(element1)
        //here ends the preparation of data based on
        //https://discourse.nodered.org/t/manipulate-vector-for-dashboard-chart-node/25298/3

        buffer = chartData;
}

msg.payload = {
