var buffer = msg.payload.observations;

var payload = [];
var topic = "";
var counter = 0;
var axis = "";
var i = 0;

switch(measurement){
    case "Peaks":
        for(let peak of buffer.X){
            payload.push(campaignTimestamp);
            payload.push(node);
            payload.push(peak);
            topic+="UPDATE frequency SET peak=1 WHERE campaignTimestamp=? AND sensor_node_ID=? and frequency=? AND axis='X';";
        }

        for(let peak of buffer.Y){
            payload.push(campaignTimestamp);
            payload.push(node);
            payload.push(peak);
            topic+="UPDATE frequency SET peak=1 WHERE campaignTimestamp=? AND sensor_node_ID=? and frequency=? AND axis='Y';";
        }

        for(let peak of buffer.Z){
            payload.push(campaignTimestamp);
            payload.push(node);
            payload.push(peak);
            topic+="UPDATE frequency SET peak=1 WHERE campaignTimestamp=? AND sensor_node_ID=? and frequency=? AND axis='Z';";
        }
        break;
    case "Frequencies":
        buffer = buffer[0].data;

        topic = "INSERT INTO frequency "+
                "(`campaignTimestamp`, `axis`, `sensor_node_ID`, "+
                "`frequency`, `amplitude`, `peak`) VALUES ";

        for(i = 0; i < buffer.length; ++i){
            switch(i){
                case 0: 
                    axis = "X";
                    break;
                case 1: 
                    axis = "Y";
                    break;
                case 2: 
                    axis = "Z";
                    break;
            }
            for(let pair of buffer[i]){
                payload.push(campaignTimestamp);
                payload.push(axis);
                payload.push(node);
                payload.push(pair.x);
                payload.push(pair.y);

                topic += "(?, ?, ?, ?, ?, 0),";
            }
        }
        topic = topic.substring(0, topic.length - 1) + ";";
        break;
    default:
        buffer = buffer[0].data;

        topic = "INSERT INTO observation "+
                "(`campaignTimestamp`, `feature`, `timestamp`, "+
                "`sensor_node_ID`, `result`) VALUES ";

        for(i = 0; i < buffer.length; ++i){
            switch(i){
                case 0: 
                    axis = "X";
                    break;
                case 1: 
                    axis = "Y";
                    break;
                case 2: 
                    axis = "Z";
                    break;
            }
            for(let pair of buffer[i]){
                payload.push(campaignTimestamp);
                payload.push(measurement.substring(0, measurement.length - 1) + " " + axis);
                payload.push(new Date(pair.x));
                payload.push(node);
                payload.push(pair.y);

                topic += "(?, ?, ?, ?, ?),";
            }
        }
        topic = topic.substring(0, topic.length - 1) + ";";
}

msg.payload = payload;
msg.topic = topic;
return msg;

